client.on("messageUpdate", async (oldMsg, newMsg) => {
  if (oldMsg.author.bot) return;
  var user = oldMsg.author;
  if (db.has(`log_${oldMsg.guild.id}`) === false) return;
  var kanal = oldMsg.guild.channels.cache.get(db.fetch(`log_${oldMsg.guild.id}`).replace("<#", "").replace(">", ""))
  if (!kanal) return;
  const embed = new Discord.MessageEmbed()
  .setColor("ORANGE")
  .addField("<a:Sistemler:788171079914225664> Kullanıcı", oldMsg.author.tag, true)
  .addField("<a:Sistemler:788171079914225664> Eski Mesaj",`  ${oldMsg.content}  `)
  .addField("<a:Sistemler:788171079914225664> Yeni Mesaj", `${newMsg.content}`)
  .setThumbnail(oldMsg.author.avatarURL)
  kanal.send(embed);  
        
    })


